import React, { useContext } from 'react';
import '../Styles/Navbar.css';
import logo_light from '../assets/logo-black.png';
import search_icon_light from '../assets/search-w.png';
import {useNavigate} from 'react-router-dom';
import { AppContext } from '../Context/AppContext';
const Navbar = () => {
  const navigate = useNavigate();
  const {isLoggedIn,userData} = useContext(AppContext);
  return (
    <div className="Navbar">
      <img src = {logo_light} alt="" className="logo" />

      {/* Search Box (Centered) */}
      <div className="search-box">
        <input type="text" placeholder="Search" />
        <img src={ search_icon_light} alt="" />
      </div>

      {/* Navigation Links (Right-Aligned) */}
      <div className="nav-links">
        <ul>
          <li onClick={()=>{navigate("/")}}>Home</li>
          <li onClick={()=>{navigate("/instructions")}}>Instructions</li>
          <li>Features</li>
          {userData ? 
          <div className='w-8 h-8 flex justify-center items-center rounded-full bg-black text-white relative group'>
          {userData.name[0]?.toUpperCase() || "U"} {/* Fallback to "U" if userData.name is missing */}
          <div className='absolute hidden group-hover:block top-0 right-0 z-10 text-black rounded pt-10'>
            <ul className='list-none m-0 p-2 bg-gray-100 text-sm'>
              {userData.Authenticated ? 
                <li onClick={logout} className='py-1 px-2 hover:bg-gray-200 cursor-pointer pr-10'>Logout</li> 
                :
                <>
                  <li onClick={()=>{navigate('/verifyEmail')}} className='py-1 px-2 hover:bg-gray-200 cursor-pointer'>Verify Email</li>
                  <li onClick={logout} className='py-1 px-2 hover:bg-gray-200 cursor-pointer pr-10'>Logout</li> 
                </>
              }
            </ul>
          </div>
        </div>  
          : 
          <li onClick={()=>{navigate("/login")}}>Login</li>}
        </ul>
      </div>

    </div>
  );
};

export default Navbar;
