import React, { useState } from 'react';
import { assets } from '../assets/assets';
import '../Styles/forgotPassword.css';
import { toast } from 'react-toastify';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const ForgotPassword = () => {
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [otp, setOtp] = useState('');
  const [showOtpField, setShowOtpField] = useState(false);

  const resetPassword = async () => {
    try {
      const { data } = await axios.post("http://localhost:7000/api/auth/forgotPassword", { email }, { withCredentials: true });
      if (data.success) {
        toast.success(data.message);
        setShowOtpField(true); // Show OTP field on success
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  return (
    <div className="reset-container">
      <div className="reset-card">
        <h1 className="text-white mb-3 fw-bold">Reset Password</h1>
        <p className="text-white mb-3 fw-bold">Enter your email to reset Password</p>

        {/* Email Input Field */}
        <div className='mb-4 flex items-center gap-3 w-full px-5 py-2.5 rounded-full bg-[#333A5C]'>
          <img src={assets.mail_icon} alt='email' />
          <input 
            onChange={(e)=>setEmail(e.target.value)} 
            className='bg-transparent outline-none text-white w-full' 
            type='email' 
            placeholder='Email id' 
            required
          />
        </div>

        {/* Submit Button */}
        <button onClick={resetPassword} className="btn btn-primary mb-4">Submit</button>

        {/* OTP Field and Submit Button (Initially Hidden) */}
        {showOtpField && (
          <div className="flex items-center gap-3 w-full">
            <div className='flex items-center gap-3 w-full px-5 py-2.5 rounded-full bg-[#333A5C] flex-grow'>
              <label htmlFor='otp' className='bg-transparent outline-none text-white'>Enter OTP:</label>
              <input 
                onChange={(e) => setOtp(e.target.value)} 
                className='bg-transparent outline-none text-white w-full' 
                type='text' 
                placeholder='Enter OTP' 
                required
              />
            </div>
            <button  className="btn btn-primary">Submit</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ForgotPassword;
