import React, { useState } from 'react';
import { assets } from '../assets/assets';
import '../Styles/forgotPassword.css'

const ForgotPassword = () => {
  const [email, setEmail] = useState('');

  const resetPassword = () => {
    // Your reset password logic
  };

  return (
    <div className="reset-container d-flex justify-content-center align-items-center">
      <div className="reset-card p-4">
        <h2 className="text-center text-white text-2xl mb-3 fw-bold display-3">Reset Password</h2>
        <div className='mb-4 flex items-center gap-3 w-full px-5 py-2.5 rounded-full bg-[#333A5C]'>
                          <img src={assets.mail_icon} alt='person' />
                          <input onChange={e=>setEmail(e.target.value)} value={email} className='bg-transparent text-white border-0 rounded px-1 py-0 w-25 focus:ring-0 focus:outline-none' type='email' placeholder='Email id' required/>
                        </div>
                        <button onClick={resetPassword} className='w-1/2 py-2 rounded-full bg-gradient-to-r from-indigo-500 to-indigo-900 text-white font-medium'>Submit</button>
      </div>
    </div>
  );
};

export default ForgotPassword;
