import React, { useContext, useEffect, useState } from 'react';
import { AppContext } from '../Context/AppContext';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../Styles/profile.css';
import { toast } from 'react-toastify';

const Profile = () => {
  const navigate = useNavigate();
  const { userData, setUserData, setIsLoggedIn } = useContext(AppContext);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const { data } = await axios.get('http://localhost:7000/api/auth/getDetails', { withCredentials: true });
        if (data.success) {
          setUserData(data.user);
        } else {
          toast.error(data.message);
          navigate('/login');
        }
      } catch (error) {
        toast.error('Failed to fetch user details');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    if (!userData) {
      fetchUserDetails();
    } else {
      setLoading(false);
    }
  }, [userData, navigate, setUserData]);

  const logout = async () => {
    try {
      const { data } = await axios.post('http://localhost:7000/api/auth/logout', {}, { withCredentials: true });
      if (data.success) {
        setIsLoggedIn(false);
        setUserData(null);
        navigate('/login');
        toast.success(data.message);
      }
    } catch (error) {
      toast.error('Logout failed');
    }
  };

  if (loading) {
    return <div className="profile-container">Loading...</div>;
  }

  return (
    <div className="profile-container">
      <h1>Profile</h1>
      <div className="profile-card">
        <p><strong>Name:</strong> {userData?.name || 'N/A'}</p>
        <p><strong>Email:</strong> {userData?.email || 'N/A'}</p>
        <p><strong>Joined:</strong> {new Date(userData?.createdAt).toLocaleDateString()}</p>
        <button onClick={logout} className="logout-btn">Logout</button>
      </div>
    </div>
  );
};

export default Profile;
