import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import axios from 'axios';

const NewPassword = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const email = location.state?.email || ""; // Get email from navigation state

  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const changePassword = async () => {
    if (!email) {
      toast.error("Email not found. Please restart the process.");
      return;
    }

    try {
      const { data } = await axios.post("http://localhost:7000/api/auth/changePassword", {
        email,
        password,
        confirmPassword,
      });

      if (data.success) {
        toast.success("Password changed successfully!");
        navigate("/login"); // Redirect to login page
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      toast.error(error.message);
    }
  };

  return (
    <div className="reset-container">
      <div className="reset-card">
        <h1 className="text-white mb-3 fw-bold">Set New Password</h1>
        <div className='mb-4 flex flex-col w-full'>
          <input 
            onChange={(e) => setPassword(e.target.value)} 
            className='bg-transparent outline-none text-white w-full px-5 py-2.5 rounded-full bg-[#333A5C]' 
            type='password' 
            placeholder='New Password' 
            required 
          />
        </div>
        <div className='mb-4 flex flex-col w-full'>
          <input 
            onChange={(e) => setConfirmPassword(e.target.value)} 
            className='bg-transparent outline-none text-white w-full px-5 py-2.5 rounded-full bg-[#333A5C]' 
            type='password' 
            placeholder='Confirm Password' 
            required 
          />
        </div>
        <button onClick={changePassword} className="btn btn-primary">Change Password</button>
      </div>
    </div>
  );
};

export default NewPassword;
