import React, { useContext, useState, useEffect } from "react";
import axios from "axios";
import { toast } from "react-toastify";
import "../Styles/profile.css";
import { AppContext } from "../Context/AppContext";

const Profile = () => {
  const { userData, setUserData } = useContext(AppContext);

  // Editable Fields
  const [name, setName] = useState(userData?.name || "");
  const [email, setEmail] = useState(userData?.email || "");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setName(userData?.name || "");
    setEmail(userData?.email || "");
  }, [userData]);

  const updateProfile = async () => {
    try {
      setLoading(true);
      const { data } = await axios.put(
        "http://localhost:7000/api/auth/updateProfile",
        { name, email },
        { withCredentials: true }
      );

      if (data.success) {
        setUserData({ ...userData, name, email });
        toast.success("Profile updated successfully!");
      } else {
        toast.error(data.message);
      }
    } catch (error) {
      toast.error("Error updating profile. Try again!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="profile-container">
      <div className="profile-card">
        <h2>Profile</h2>
        <img src="https://via.placeholder.com/120" alt="Profile" className="profile-avatar" />

        {/* Editable Fields */}
        <div className="profile-info">
          <label><strong>Name:</strong></label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />

          <label><strong>Email:</strong></label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} readOnly /> 

          <p><strong>Joined:</strong> {new Date(userData?.createdAt).toLocaleDateString()}</p>
        </div>

        {/* Update Button */}
        <button onClick={updateProfile} className="update-btn" disabled={loading}>
          {loading ? "Updating..." : "Update Profile"}
        </button>
      </div>
    </div>
  );
};

export default Profile;
